/* JS-like pseudo code */

let scores = { E:0, I:0, S:0, N:0, T:0, F:0, J:0, P:0 };
userAnswers.forEach(ans => {
  scores[ans] += 1;
});

const finalMBTI = (
  (scores['E'] >= scores['I'] ? 'E' : 'I') +
  (scores['S'] >= scores['N'] ? 'S' : 'N') +
  (scores['T'] >= scores['F'] ? 'T' : 'F') +
  (scores['J'] >= scores['P'] ? 'J' : 'P')
);

console.log(finalMBTI);
